function getVotes(t){return fetch("https://us-informant-senate.herokuapp.com"+t).then(t=>t.json())}function makeVoteArticles(t){return t.articles.length>=1?t.articles.map(t=>`<div class="card">\n           <div class="card-body">\n             <h5 class="card-title">${t.title}</h5>\n             <p class="card-text">${t.description}</p>\n             <a href="${t.url}" class="btn btn-raised btn-success" target="_blank">Source: ${t.source}</a>\n             <a href="/share?article=${t.id}&url=${t.url}" data-confirm="Confirm that you would like to create a tweet. The tweet will include a link to Informant." class="btn btn-raised btn-info">Share on Twitter</a>\n           </div>\n        </div>`).join(""):'<div class="card">\n      <div class="card-body">\n        <h5 class="card-title">No News Found</h5>\n      </div>\n    </div>'}function makeVoteCard(t,a){console.log(t);let n=t.map(t=>`<div class="col-sm-6"\n        <div class="card mb-3 vote">\n          <div class="card-header">\n            <h3>${t.number} ${t.created_at}</h3>\n          </div>\n\n          <div class="card-body">\n            <h5 class="display-3">\n              ${t.question}\n            </h5>\n            <h3 class="card-text">\n              ${t.description}\n            </h3>\n            <h3 class="card-text">\n              Result: ${t.result}\n            </h3>\n          </div>\n\n          <div class="card-footer">\n            <button class="btn" type="button" data-toggle="collapse" data-target="#collapse-${t.id}" aria-expanded="false" aria-controls="collapse-${t.id}">\n              related news\n            </button>\n            <div class="collapse vote-article-cards" id="collapse-${t.id}">\n              ${makeVoteArticles(t)}\n            </div>\n          </div>\n        </div>\n      </div>`);$(a).html(n)}jQuery(function(t){t("a[data-confirm], input[data-confirm]").live("click",function(){if(!confirm(t(this).attr("data-confirm")))return!1})}),getVotes("/api/v1/votes").then(t=>makeVoteCard(t,".vote-list"));