function getVotes(a){return fetch("https://us-informant-senate.herokuapp.com"+a).then(a=>a.json())}function makeVoteArticles(a){return a.articles.length>=1?a.articles.map(a=>`<div class="card">\n           <div class="card-body">\n             <h5 class="card-title">${a.title}</h5>\n             <p class="card-text">${a.description}</p>\n             <a href="${a.url}" class="btn" target="_blank">${a.source}</a>\n             <a href="/share?article=${a.id}&url=${a.url}" data-confirm="Confirm Tweet" class="btn">Share on Twitter</a>\n           </div>\n        </div>`).join(""):'<div class="card">\n      <div class="card-body">\n        <h5 class="card-title">No News Found</h5>\n      </div>\n    </div>'}function makeVoteCard(a,t){console.log(a);let n=a.map(a=>`<div class="col-sm-6"\n        <div class="card mb-3 vote">\n          <div class="card-header">\n            <h3>${a.number} ${a.created_at}</h3>\n          </div>\n\n          <div class="card-body">\n            <h5 class="display-3">\n              ${a.question}\n            </h5>\n            <h3 class="card-text">\n              ${a.description}\n            </h3>\n            <h3 class="card-text">\n              Result: ${a.result}\n            </h3>\n          </div>\n\n          <div class="card-footer">\n            <button class="btn" type="button" data-toggle="collapse" data-target="#collapse-${a.id}" aria-expanded="false" aria-controls="collapse-${a.id}">\n              related news\n            </button>\n            <div class="collapse vote-article-cards" id="collapse-${a.id}">\n              ${makeVoteArticles(a)}\n            </div>\n          </div>\n        </div>\n      </div>`);$(t).html(n)}jQuery(function(a){a("a[data-confirm], input[data-confirm]").live("click",function(){if(!confirm(a(this).attr("data-confirm")))return!1})}),getVotes("/api/v1/votes").then(a=>makeVoteCard(a,".vote-list"));